mapper.select:
select

  distinct


from sys_role




  order by ${orderByClause}


  limit #{limitStart} , #{limitSize}

mapper.insert:
insert into sys_role (ID, NAME, IS_EXPIRED, 
  IS_LOCKED, IS_ENABLED, TYPE, 
  STATUS, CREATE_BY, CREATE_TIME, 
  UPDATE_BY, UPDATE_TIME, EN_NAME
  )
values (#{id,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{isExpired,jdbcType=TINYINT}, 
  #{isLocked,jdbcType=TINYINT}, #{isEnabled,jdbcType=TINYINT}, #{type,jdbcType=INTEGER}, 
  #{status,jdbcType=INTEGER}, #{createBy,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{updateBy,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}, #{enName,jdbcType=VARCHAR}
  )

mapper.select:
SELECT a.*, e.ID AS PERMISSION_ID, e. NAME AS PERMISSION_NAME, e.IS_EXPIRED AS PERMISSION_IS_EXPIRED, e.IS_LOCKED AS PERMISSION_IS_LOCKED, e.IS_ENABLED AS PERMISSION_IS_ENABLED, e.TYPE AS PERMISSION_TYPE, e.STATUS AS PERMISSION_STATUS, e.CREATE_BY AS PERMISSION_CREATE_BY, e.CREATE_TIME AS PERMISSION_CREATE_TIME, e.UPDATE_BY AS PERMISSION_UPDATE_BY, e.UPDATE_TIME AS PERMISSION_UPDATE_TIME, e.EN_NAME AS PERMISSION_EN_NAME, e.PARENT_ID AS PERMISSION_PARENT_ID, e.CODE AS PERMISSION_CODE, e.IS_MENU AS PERMISSION_IS_MENU, e.URL AS PERMISSION_URL FROM sys_role AS a LEFT JOIN sys_role_permission AS d ON a.ID = d.ROLE_ID LEFT JOIN sys_permission AS e ON d.PERMISSION_ID = e.ID WHERE a.id = #{id}
union
SELECT a.*, e.ID AS PERMISSION_ID, e. NAME AS PERMISSION_NAME, e.IS_EXPIRED AS PERMISSION_IS_EXPIRED, e.IS_LOCKED AS PERMISSION_IS_LOCKED, e.IS_ENABLED AS PERMISSION_IS_ENABLED, e.TYPE AS PERMISSION_TYPE, e.STATUS AS PERMISSION_STATUS, e.CREATE_BY AS PERMISSION_CREATE_BY, e.CREATE_TIME AS PERMISSION_CREATE_TIME, e.UPDATE_BY AS PERMISSION_UPDATE_BY, e.UPDATE_TIME AS PERMISSION_UPDATE_TIME, e.EN_NAME AS PERMISSION_EN_NAME, e.PARENT_ID AS PERMISSION_PARENT_ID, e.CODE AS PERMISSION_CODE, e.IS_MENU AS PERMISSION_IS_MENU, e.URL AS PERMISSION_URL FROM sys_role AS a LEFT JOIN sys_role_permission AS d ON a.ID = d.ROLE_ID LEFT JOIN sys_permission AS e ON d.PERMISSION_ID = e.ID WHERE a.name = #{id}
